INSERT INTO `carte_carte` (`CarteID`, `Nom`, `Extension`, `Type`, `Cout`, `Texte`, `Gold`, `Influence`, `Initiative`, `Force`, `Prise`, `Image`) VALUES
(1, 'Cersei Lannister', 7, 1, 4, 'Cersei Lannister does not kneel to attack during intrigue challenges. Response: After you win an intrigue challenge, Cersei Lannister claims 1 power. Then, Cersei Lannister gains your choice of stealth or deadly until the end of the phase.', NULL, NULL, NULL, 3, NULL, 'lions6.jpg'),
(2, 'Stannis Baratheon', 50, 1, 4, 'Immune to location abilities. While Stannis Baratheon is the only King character in play, he gains vigilant, renown, and stealth.', NULL, NULL, NULL, 3, NULL, 'narrow7.jpg'),
(3, 'Ser Arys Oakheart', 3, 1, 3, 'Renown. No attachments except weapon. Response: After Ser Arys Oakheart comes into play,choose and discard 1 Ally or Mercenary character from play.', NULL, NULL, NULL, 3, NULL, 'pots9.jpg'),
(4, 'Rhaegal', 6, 1, 4, 'Targaryen only. No attachments. Ambush. Stealth. Response: After you win a challenge in which Rhaegal participated and all of your participating characters have the Dragon trait, each participating character you control claims 1 power. (Limit once per phase)', NULL, NULL, NULL, 4, NULL, 'dragon24.jpg'),
(5, 'Feuding Clanswoman', 7, 1, 2, 'No attachments. Stealth. Initiative +2', NULL, NULL, 2, 1, NULL, 'lions38.jpg'),
(6, 'Drowned Crewman', 49, 1, 2, 'If you have more than 1 opponent Drowned Crewman gains: ''Marshalling: Kneel Drowned Crewman to choose an opponent with more cards in his or her discard pile than you. Move 1 gold token from that player''s gold pool to your own.', NULL, NULL, NULL, 2, NULL, 'champions108.jpg'),
(7, 'Winterfell Reserves', 1, 1, 3, 'Response : after Winterfell Reserves is declared as a defender, pay one gold to put the top card of a player''s discard pile on top of his or her deck.', NULL, NULL, NULL, 3, NULL, 'coreset35.jpg'),
(8, 'Lannisport Treasury', 7, 2, 2, 'Limited.', 2, NULL, NULL, NULL, NULL, 'lions30.jpg'),
(9, 'Northern Fiefdoms', 1, 2, 1, 'Limited. Marshalling: Kneel Northern Fiefdoms to lower the cost of the next Stark card you play this phase by 1.', NULL, 1, NULL, NULL, NULL, 'coreset38.jpg'),
(10, 'Kingsroad Fiefdom', 6, 2, 2, '', NULL, 2, 2, NULL, NULL, 'dragon42.jpg'),
(11, 'Kraken Tattoo', 20, 4, 0, 'Attached character gains the Ironborn trait. Response : after a card is discarded from a player''s deck, attached character gets +1 STR and Intimidate until the end of the phase.', NULL, NULL, NULL, NULL, NULL, 'landing10.jpg'),
